import React, { useState } from "react";
import MessageForm from "./components/MessageForm";
import MessageList from "./components/MessageList";
import { sendMessage } from "./services/api";

const App = () => {
  const [messages, setMessages] = useState([]);

  const handleSend = async (message) => {
    // Add user message immediately
    setMessages((prev) => [...prev, { text: message, isUser: true }]);

    // Add a temporary "typing..." message from AI
    const typingMessage = { text: "Typing...", isUser: false, isTyping: true };
    setMessages((prev) => [...prev, typingMessage]);

    try {
      // Send message to the backend
      const response = await sendMessage(message);

      if (response && response.response) {
        // Remove "Typing..." message before showing AI response
        setMessages((prev) => [
          ...prev.filter((msg) => !msg.isTyping),
          { text: response.response, isUser: false, isTyping: false },
        ]);
      }
    } catch (error) {
      console.error("Error fetching AI response:", error);
    }
  };


  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
      <h2 className="text-2xl font-bold mb-4">Chat App</h2>
      <MessageList messages={messages} />
      <MessageForm onSend={handleSend} />
      <p className="text-sm text-gray-500 mt-4">Debug: If this text is visible, rendering works.</p>
    </div>
  );
};

export default App;
