import axios from "axios";

const API_URL = "http://127.0.0.1:8000/message"; // Your FastAPI backend URL

export const sendMessage = async (message) => {
  try {
    const response = await axios.post(API_URL, {
      user_id: 1, // Static user ID (can be dynamic)
      content: message, // User's message
    }, {
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      }
    });

    console.log("Full API Response:", response.data); // Debugging

    return response.data; // Return full response object
  } catch (error) {
    console.error("Error sending message:", error);
    return { response: "Server error, please try again!" };
  }
};
