import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [chats, setChats] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [selectedChat, setSelectedChat] = useState(null);

  const token = localStorage.getItem("token");
  const headers = { Authorization: `Bearer ${token}` };

  useEffect(() => {
    axios.get("http://localhost:8000/admin/users", { headers }).then((res) => {
      setUsers(res.data);
    });
  }, []);

  const loadChats = (userId) => {
    setSelectedUser(userId);
    axios.get(`http://localhost:8000/admin/chats/${userId}`, { headers }).then((res) => {
      setChats(res.data);
      setMessages([]);
    });
  };

  const loadMessages = (chatId) => {
    setSelectedChat(chatId);
    axios.get(`http://localhost:8000/admin/messages/${chatId}`, { headers }).then((res) => {
      setMessages(res.data);
    });
  };

  const deleteMessage = (messageId) => {
    axios.delete(`http://localhost:8000/admin/messages/${messageId}`, { headers }).then(() => {
      setMessages((prev) => prev.filter((msg) => msg.id !== messageId));
    });
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-4">Admin Panel</h1>

      <div className="grid grid-cols-3 gap-4">
        {/* Users List */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-2">Users</h2>
          {users.map((user) => (
            <div
              key={user.id}
              onClick={() => loadChats(user.id)}
              className="cursor-pointer text-blue-600 hover:underline"
            >
              {user.email}
            </div>
          ))}
        </div>

        {/* Chats List */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-2">Chats</h2>
          {chats.map((chat) => (
            <div
              key={chat.id}
              onClick={() => loadMessages(chat.id)}
              className="cursor-pointer text-green-600 hover:underline"
            >
              Chat #{chat.id}
            </div>
          ))}
        </div>

        {/* Messages List */}
        <div className="bg-white p-4 shadow rounded">
          <h2 className="text-xl font-semibold mb-2">Messages</h2>
          {messages.map((msg) => (
            <div key={msg.id} className="border-b py-2">
              <div><strong>User:</strong> {msg.content}</div>
              <div><strong>Bot:</strong> {msg.response}</div>
              <button
                onClick={() => deleteMessage(msg.id)}
                className="mt-1 text-red-600 hover:underline text-sm"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
