import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { motion } from "framer-motion";

const botName = "Chatbot";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [userMessage, setUserMessage] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const speak = (text) => {
    if ("speechSynthesis" in window) {
      const utterance = new SpeechSynthesisUtterance(text);
      speechSynthesis.speak(utterance);
    }
  };

  const handleSend = async () => {
    if (!userMessage.trim()) return;

    const userToken = localStorage.getItem("token"); // Retrieve token from login

    setMessages((prev) => [...prev, { text: userMessage, isUser: true }]);
    setUserMessage("");
    setIsTyping(true);

    try {
      const response = await axios.post(
        "http://localhost:8000/message",
        { content: userMessage },
        {
          headers: { Authorization: `Bearer ${userToken}` }, // Pass user token
        }
      );

      setIsTyping(false);
      const botReply = response.data.response || "I didn't understand that.";

      let index = 0;
      const interval = setInterval(() => {
        setMessages((prev) => [
          ...prev.slice(0, -1),
          { text: botReply.substring(0, index + 1), isUser: false },
        ]);
        index++;
        if (index >= botReply.length) {
          clearInterval(interval);
          speak(botReply);
        }
      }, 50);

      setMessages((prev) => [...prev, { text: "", isUser: false }]);
    } catch (error) {
      setIsTyping(false);
      setMessages((prev) => [...prev, { text: "Error: Unable to reach AI.", isUser: false }]);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
      <div className="max-w-lg w-full bg-gray-800 p-6 rounded-lg shadow-lg">
        <h2 className="text-center text-2xl font-bold mb-4">Chat with {botName}</h2>
        <div className="h-96 overflow-y-auto p-4 space-y-4 bg-gray-700 rounded-lg">
          {messages.map((msg, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: msg.isUser ? 50 : -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3 }}
              className={`p-3 rounded-lg max-w-xs ${
                msg.isUser ? "bg-blue-500 text-white ml-auto" : "bg-gray-600 text-white"
              }`}
            >
              <strong>{msg.isUser ? "You" : botName}:</strong> {msg.text}
            </motion.div>
          ))}
          {isTyping && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, repeat: Infinity }}
              className="p-3 bg-gray-600 text-white rounded-lg max-w-xs"
            >
              <strong>{botName}:</strong> Typing...
            </motion.div>
          )}
          <div ref={messagesEndRef} />
        </div>

        <div className="flex items-center mt-4">
          <input
            type="text"
            value={userMessage}
            onChange={(e) => setUserMessage(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
            placeholder="Type your message..."
            className="flex-1 p-3 text-white bg-gray-700 rounded-lg outline-none"
          />
          <button onClick={handleSend} className="ml-2 p-2 bg-blue-500 rounded-full text-white">
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
