import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [chats, setChats] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState(null);
  const [selectedChatId, setSelectedChatId] = useState(null);
  const token = localStorage.getItem("token");

  useEffect(() => {
    axios.get("http://localhost:8000/admin/users", {
      headers: { Authorization: `Bearer ${token}` },
    })
    .then(res => setUsers(res.data))
    .catch(err => console.error("Failed to fetch users", err));
  }, [token]);

  const fetchChats = (userId) => {
    setSelectedUserId(userId);
    setSelectedChatId(null);
    setMessages([]);
    axios.get(`http://localhost:8000/admin/users/${userId}/chats`, {
      headers: { Authorization: `Bearer ${token}` },
    })
    .then(res => setChats(res.data))
    .catch(err => console.error("Failed to fetch chats", err));
  };

  const fetchMessages = (chatId) => {
    setSelectedChatId(chatId);
    axios.get(`http://localhost:8000/admin/chats/${chatId}/messages`, {
      headers: { Authorization: `Bearer ${token}` },
    })
    .then(res => setMessages(res.data))
    .catch(err => console.error("Failed to fetch messages", err));
  };

  const handleDelete = (msgId) => {
    axios.delete(`http://localhost:8000/admin/messages/${msgId}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
    .then(() => {
      setMessages(messages.filter(m => m.id !== msgId));
    })
    .catch(err => console.error("Failed to delete", err));
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-4">🛠️ Admin Panel</h1>
      <div className="grid grid-cols-3 gap-4">
        {/* Users */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="font-semibold mb-2">Users</h2>
          {users.map(user => (
            <p key={user.id} className="cursor-pointer text-blue-600" onClick={() => fetchChats(user.id)}>
              {user.email}
            </p>
          ))}
        </div>

        {/* Chats */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="font-semibold mb-2">Chats</h2>
          {chats.map(chat => (
            <p key={chat.id} className="cursor-pointer text-green-700" onClick={() => fetchMessages(chat.id)}>
              Chat #{chat.id}
            </p>
          ))}
        </div>

        {/* Messages */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="font-semibold mb-2">Messages</h2>
          {messages.map(msg => (
            <div key={msg.id} className="border-b pb-2 mb-2">
              <p className="text-gray-800 mb-1"><strong>User:</strong> {msg.content}</p>
              <p className="text-gray-600 mb-1"><strong>Bot:</strong> {msg.response}</p>
              <button onClick={() => handleDelete(msg.id)} className="text-red-500 text-sm">
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
