import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Chatbot from "./pages/Chatbot";
import Login from "./pages/Login";
import Signup from "./pages/Signup";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // ✅ Check authentication status on mount
  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsAuthenticated(!!token);
  }, []);

  return (
    <Router>
      <Routes>
        {/* ✅ If not authenticated, force user to login/signup */}
        <Route path="/" element={isAuthenticated ? <Navigate to="/chat" /> : <Navigate to="/login" />} />

        <Route path="/login" element={isAuthenticated ? <Navigate to="/chat" /> : <Login />} />
        <Route path="/signup" element={isAuthenticated ? <Navigate to="/chat" /> : <Signup />} />

        {/* ✅ Protect chat page (must be logged in) */}
        <Route path="/chat" element={isAuthenticated ? <Chatbot /> : <Navigate to="/login" />} />

        {/* ✅ Catch-all: Redirect unknown paths */}
        <Route path="*" element={<Navigate to={isAuthenticated ? "/chat" : "/login"} />} />
      </Routes>
    </Router>
  );
};

export default App;
