import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// ðŸ”¹ Global token variable (temporary storage before localStorage)
let globalToken = null;

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isTokenChecked, setIsTokenChecked] = useState(false); // Prevents infinite loops

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const token = urlParams.get("token");

    if (token) {
      console.log("âœ… Google login successful, storing token:", token);
      
      globalToken = token; // ðŸ”¹ Store in global variable
      localStorage.setItem("token", token);
      sessionStorage.setItem("token", token);

      setTimeout(() => {
        if (localStorage.getItem("token") || sessionStorage.getItem("token")) {
          navigate("/chat", { replace: true });
        }
      }, 200); // âœ… Short delay ensures storage before navigation
    } else {
      const storedToken = globalToken || localStorage.getItem("token") || sessionStorage.getItem("token");

      console.log("ðŸ”¹ Checking stored token:", storedToken);
      if (storedToken && !isTokenChecked) {
        setIsTokenChecked(true); // âœ… Prevents infinite re-renders
        navigate("/chat", { replace: true });
      }
    }
  }, [navigate, isTokenChecked]); // âœ… Ensures it only runs once

  // âœ… Handle Standard Login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log("ðŸ”¹ Sending login data:", { email, password });

      const response = await axios.post(
        "http://localhost:8000/login",
        { email, password },
        {
          headers: { "Content-Type": "application/json" },
          withCredentials: true,
        }
      );

      if (response.data.token) {
        globalToken = response.data.token; // ðŸ”¹ Store in global variable
        localStorage.setItem("token", response.data.token);
        sessionStorage.setItem("token", response.data.token);
        alert("âœ… Login successful!");
        navigate("/chat", { replace: true });
      } else {
        throw new Error("Invalid response from server");
      }
    } catch (error) {
      console.error("âŒ Login Error:", error.response ? error.response.data : error.message);
      setError(error.response?.data?.detail || "Invalid credentials. Please try again.");
    }
  };

  // âœ… Handle Google Login
  const handleGoogleLogin = () => {
    window.location.href = "http://localhost:8000/auth/google";
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
      <div className="max-w-md w-full bg-gray-800 p-6 rounded-lg shadow-lg">
        <h2 className="text-center text-2xl font-bold mb-4">Login</h2>

        {error && <p className="text-red-500 text-center">{error}</p>}

        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            className="w-full p-3 text-white bg-gray-700 rounded-lg outline-none"
            required
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            className="w-full p-3 text-white bg-gray-700 rounded-lg outline-none"
            required
          />
          <button type="submit" className="w-full p-3 bg-blue-500 rounded-lg text-white">
            Login
          </button>
        </form>

        <button
          onClick={handleGoogleLogin}
          className="w-full p-3 mt-4 bg-red-500 rounded-lg text-white"
        >
          Login with Google
        </button>

        <p className="text-center mt-4">
          Don't have an account?{" "}
          <span className="text-blue-400 cursor-pointer" onClick={() => navigate("/signup")}>
            Sign up
          </span>
        </p>
      </div>
    </div>
  );
};

export default Login;
