import React, { useState, useEffect, useRef } from "react";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";
import { getMessages, sendMessage, getChats, createNewChat } from "../services/api";

const botName = "Chatbot";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [userMessage, setUserMessage] = useState("");
  const [chats, setChats] = useState([]);
  const [activeChat, setActiveChat] = useState(null);
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();

  // ✅ Check for authentication
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/");
    } else {
      fetchChats();
    }
  }, [navigate]);

  // ✅ Fetch all user chats
  const fetchChats = async () => {
    try {
      const data = await getChats();
      setChats(data.chats);
      if (data.chats.length > 0) {
        setActiveChat(data.chats[0].id);
        fetchMessages(data.chats[0].id);
      }
    } catch (error) {
      console.error("Error fetching chats:", error);
    }
  };

  // ✅ Fetch messages for selected chat
  const fetchMessages = async (chatId) => {
    try {
      const data = await getMessages(chatId);
      setMessages(data.messages);
    } catch (error) {
      console.error("Error fetching messages:", error);
    }
  };

  // ✅ Handle creating a new chat
  const handleNewChat = async () => {
    try {
      const response = await createNewChat();
      setChats([...chats, response.chat_id]);
      setActiveChat(response.chat_id);
      setMessages([]);
    } catch (error) {
      console.error("Error creating new chat:", error);
    }
  };

  // ✅ Handle sending a message
  const handleSend = async () => {
    if (!userMessage.trim() || !activeChat) return;

    setMessages((prev) => [...prev, { text: userMessage, isUser: true }]);
    setUserMessage("");

    try {
      const response = await sendMessage(activeChat, userMessage);
      setMessages((prev) => [...prev, { text: response.response, isUser: false }]);
    } catch (error) {
      console.error("Error sending message:", error);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white p-4">
      <div className="max-w-lg w-full bg-gray-800 p-6 rounded-lg shadow-lg">
        <h2 className="text-center text-2xl font-bold mb-4">Chat with {botName}</h2>

        {/* ✅ Chat Selection List */}
        <div className="mb-4 flex gap-2">
          <button onClick={handleNewChat} className="p-2 bg-green-500 text-white rounded-lg">
            New Chat
          </button>
          {chats.map((chat) => (
            <button
              key={chat.id}
              onClick={() => {
                setActiveChat(chat.id);
                fetchMessages(chat.id);
              }}
              className={`p-2 rounded-lg ${chat.id === activeChat ? "bg-blue-500" : "bg-gray-700"}`}
            >
              Chat {chat.id}
            </button>
          ))}
        </div>

        {/* ✅ Message List */}
        <div className="h-96 overflow-y-auto p-4 space-y-4 bg-gray-700 rounded-lg">
          {messages.map((msg, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: msg.isUser ? 50 : -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3 }}
              className={`p-3 rounded-lg max-w-xs ${msg.isUser ? "bg-blue-500 text-white ml-auto" : "bg-gray-600 text-white"}`}
            >
              <strong>{msg.isUser ? "You" : botName}:</strong> {msg.text}
            </motion.div>
          ))}
        </div>

        {/* ✅ Input Field */}
        <div className="flex items-center mt-4">
          <input
            type="text"
            value={userMessage}
            onChange={(e) => setUserMessage(e.target.value)}
            onKeyDown={(e) => e.key === "Enter" && handleSend()}
            placeholder="Type your message..."
            className="flex-1 p-3 text-white bg-gray-700 rounded-lg outline-none"
          />
          <button onClick={handleSend} className="ml-2 p-2 bg-blue-500 rounded-full text-white">
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
