import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Chatbot from "./pages/Chatbot";
import Login from "./pages/Login";
import Signup from "./pages/Signup";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(null);

  useEffect(() => {
    // Check if the user has a token stored
    const token = localStorage.getItem("token");
    setIsAuthenticated(!!token);
  }, []);

  // Prevent app from rendering before authentication check is complete
  if (isAuthenticated === null) {
    return <div className="text-center text-white">Loading...</div>;
  }

  return (
    <Router>
      <Routes>
        {/* Default page redirects to login if not authenticated */}
        <Route path="/" element={isAuthenticated ? <Navigate to="/chat" /> : <Navigate to="/login" />} />

        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />

        {/* Chat route is only accessible if authenticated */}
        <Route path="/chat" element={isAuthenticated ? <Chatbot /> : <Navigate to="/login" />} />

        {/* Catch-all route: Redirects based on authentication */}
        <Route path="*" element={<Navigate to={isAuthenticated ? "/chat" : "/login"} />} />
      </Routes>
    </Router>
  );
};

export default App;
