import React, { useEffect, useState } from "react";
import axios from "axios";
import { motion } from "framer-motion";

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [chats, setChats] = useState([]);
  const [messages, setMessages] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState(null);
  const [selectedChatId, setSelectedChatId] = useState(null);
  const token = localStorage.getItem("token");

  useEffect(() => {
    axios.get("http://localhost:8000/admin/users", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => setUsers(res.data))
      .catch(err => console.error("Failed to fetch users", err));
  }, [token]);

  const fetchChats = (userId) => {
    setSelectedUserId(userId);
    setSelectedChatId(null);
    setMessages([]);
    axios.get(`http://localhost:8000/admin/users/${userId}/chats`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => setChats(res.data))
      .catch(err => console.error("Failed to fetch chats", err));
  };

  const fetchMessages = (chatId) => {
    setSelectedChatId(chatId);
    axios.get(`http://localhost:8000/admin/chats/${chatId}/messages`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => setMessages(res.data))
      .catch(err => console.error("Failed to fetch messages", err));
  };

  const handleDelete = (msgId) => {
    axios.delete(`http://localhost:8000/admin/messages/${msgId}`, {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(() => {
        setMessages(messages.filter(m => m.id !== msgId));
      })
      .catch(err => console.error("Failed to delete", err));
  };

  return (
    <div className="min-h-screen p-8 bg-gradient-to-br from-gray-100 to-gray-200">
      <h1 className="text-4xl font-extrabold mb-6 text-gray-800">👑 Admin Panel</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Users */}
        <motion.div className="bg-white p-6 rounded-lg shadow-lg" initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }}>
          <h2 className="text-xl font-bold mb-4">👤 Users</h2>
          {users.length === 0 ? (
            <p className="text-gray-500">No users found.</p>
          ) : (
            users.map(user => (
              <p
                key={user.id}
                onClick={() => fetchChats(user.id)}
                className="cursor-pointer text-blue-600 hover:underline hover:text-blue-800 transition"
              >
                {user.email}
              </p>
            ))
          )}
        </motion.div>

        {/* Chats */}
        <motion.div className="bg-white p-6 rounded-lg shadow-lg" initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.1 }}>
          <h2 className="text-xl font-bold mb-4">💬 Chats</h2>
          {chats.length === 0 ? (
            <p className="text-gray-500">Select a user to view chats.</p>
          ) : (
            chats.map(chat => (
              <p
                key={chat.id}
                onClick={() => fetchMessages(chat.id)}
                className="cursor-pointer text-green-700 hover:underline hover:text-green-900 transition"
              >
                Chat #{chat.id}
              </p>
            ))
          )}
        </motion.div>

        {/* Messages */}
        <motion.div className="bg-white p-6 rounded-lg shadow-lg" initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.2 }}>
          <h2 className="text-xl font-bold mb-4">📨 Messages</h2>
          {messages.length === 0 ? (
            <p className="text-gray-500">Select a chat to view messages.</p>
          ) : (
            messages.map(msg => (
              <motion.div
                key={msg.id}
                className="border-b pb-2 mb-3"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
              >
                <p className="text-gray-800 mb-1"><strong>User:</strong> {msg.content}</p>
                <p className="text-gray-600 mb-1"><strong>Bot:</strong> {msg.response}</p>
                <button
                  onClick={() => handleDelete(msg.id)}
                  className="text-red-500 hover:text-red-700 text-sm"
                >
                  🗑 Delete
                </button>
              </motion.div>
            ))
          )}
        </motion.div>
      </div>
    </div>
  );
};

export default AdminPanel;
